                        *current_weight = *current_weight - weights[j] + weights[i];
                        for k in 0..num_items {
                            marginal_gains[k] = marginal_gains[k] - interactions[j][k] as i64 + interactions[i][k] as i64;
                        }
                    }
                }
            } else {
