use crate::challenge::{Challenge, Solution};
use anyhow::{anyhow, Result};
use serde::{Deserialize, Serialize};
use serde_json::{Map, Value};
use rand::{rngs::SmallRng, Rng, SeedableRng};

pub fn solve_challenge(
    challenge: &Challenge,
    save_solution: &dyn Fn(&Solution) -> Result<()>,
    _hyperparameters: &Option<Map<String, Value>>,
) -> Result<()> {
    // EVOLVE-BLOCK-START    
    #[derive(Serialize, Deserialize)]
    pub struct Hyperparameters {
        // Optionally define hyperparameters here
    }

    // This implements a greedy constructive heuristic for the Quadratic Knapsack Problem.
    // The algorithm iteratively adds the item with the highest "marginal density"
    // to the solution. The marginal density is the item's marginal value (its base
    // value plus the sum of interaction values with items already in the knapsack)
    // divided by its weight. This approach is more sophisticated than a simple
    // value-to-weight ratio as it accounts for the quadratic interaction terms.

    let num_items = challenge.values.len();
    let mut selected = Vec::new();
    let mut current_weight: u32 = 0;

    // Use a boolean mask to efficiently track which items are not yet selected.
    let mut unselected_mask: Vec<bool> = vec![true; num_items];

    // `marginal_gains[i]` stores `values[i] + sum(interaction_values[i][j])` for all `j`
    // currently in `selected`. We use i64 to prevent potential overflow from summing u32 and i32.
    let mut marginal_gains: Vec<i64> = challenge.values.iter().map(|&v| v as i64).collect();

    loop {
        let mut best_item: Option<usize> = None;
        let mut best_density: f64 = f64::NEG_INFINITY;

        // Find the unselected item with the best marginal density that still fits.
        for i in 0..num_items {
            if unselected_mask[i] {
                let item_weight = challenge.weights[i];
                if current_weight + item_weight <= challenge.max_weight {
                    // Guard against division by zero, though weights are typically positive.
                    if item_weight > 0 {
                        let density = marginal_gains[i] as f64 / item_weight as f64;
                        if density > best_density {
                            best_density = density;
                            best_item = Some(i);
                        }
                    }
                }
            }
        }

        if let Some(item_to_add) = best_item {
            // Add the best item to the solution.
            selected.push(item_to_add);
            current_weight += challenge.weights[item_to_add];
            unselected_mask[item_to_add] = false;

            // Update the marginal gains of remaining unselected items. This is an O(N) step.
            // When an item is added, the potential value of adding other items changes.
            for i in 0..num_items {
                if unselected_mask[i] {
                    marginal_gains[i] += challenge.interaction_values[item_to_add][i] as i64;
                }
            }
        } else {
            // If no item can be added (either none fit or all are considered), we're done.
            break;
        }
    }
    // EVOLVE-BLOCK-END

    save_solution(&Solution { items: selected })?;
    Ok(())
}

