use crate::challenge::{Challenge, Solution};
use anyhow::{anyhow, Result};
use serde::{Deserialize, Serialize};
use serde_json::{Map, Value};
use rand::{rngs::SmallRng, Rng, SeedableRng};

pub fn solve_challenge(
    challenge: &Challenge,
    save_solution: &dyn Fn(&Solution) -> Result<()>,
    _hyperparameters: &Option<Map<String, Value>>,
) -> Result<()> {
    // EVOLVE-BLOCK-START    
    #[derive(Serialize, Deserialize)]
    pub struct Hyperparameters {
        pub max_iterations: usize,
        pub initial_temperature: f64,
        pub cooling_rate: f64,
        pub swap_probability: f64,
    }

    // Initialize hyperparameters, prioritizing provided values or using robust defaults for Simulated Annealing
    let hyperparameters = match _hyperparameters {
        Some(params) => {
            serde_json::from_value::<Hyperparameters>(Value::Object(params.clone()))
                .unwrap_or_else(|_| Hyperparameters {
                    max_iterations: 1_000_000,
                    initial_temperature: 500.0,
                    cooling_rate: 0.99999,
                    swap_probability: 0.6,
                })
        }
        None => Hyperparameters {
            max_iterations: 1_000_000,
            initial_temperature: 500.0,
            cooling_rate: 0.99999,
            swap_probability: 0.6,
        },
    };

    let num_items = challenge.values.len();
    let max_weight = challenge.max_weight;
    let weights = &challenge.weights;
    let values = &challenge.values;
    let interactions = &challenge.interaction_values;

    let mut rng = SmallRng::from_seed(challenge.seed);

    // --- Helper function to calculate total value (O(N^2)) ---
    #[inline]
    fn calculate_value(
        is_selected: &[bool],
        values: &[u32],
        interactions: &[Vec<i32>],
        n: usize,
    ) -> i64 {
        let mut total_value: i64 = 0;
        for i in 0..n {
            if is_selected[i] {
                total_value += values[i] as i64;
                for j in (i + 1)..n {
                    if is_selected[j] {
                        total_value += interactions[i][j] as i64;
                    }
                }
            }
        }
        total_value
    }

    // --- Initialization: Linear Greedy Heuristic ---
    let mut current_is_selected = vec![false; num_items];
    let mut current_weight: u32 = 0;

    // Sort by V/W ratio for initial feasible solution
    let mut items_sorted: Vec<usize> = (0..num_items).collect();
    items_sorted.sort_by(|&a, &b| {
        let ratio_a = values[a] as f64 / weights[a] as f64;
        let ratio_b = values[b] as f64 / weights[b] as f64;
        ratio_b.partial_cmp(&ratio_a).unwrap_or(std::cmp::Ordering::Equal)
    });

    for item_idx in items_sorted {
        if current_weight.checked_add(weights[item_idx]).map_or(false, |w| w <= max_weight) {
            current_is_selected[item_idx] = true;
            current_weight += weights[item_idx];
        }
    }

    let mut current_value = calculate_value(&current_is_selected, values, interactions, num_items);
    let mut best_is_selected = current_is_selected.clone();
    let mut best_value = current_value;

    // Pre-calculate selected/unselected indices for fast random access
    let mut selected_indices: Vec<usize> = (0..num_items).filter(|&i| current_is_selected[i]).collect();
    let mut unselected_indices: Vec<usize> = (0..num_items).filter(|&i| !current_is_selected[i]).collect();

    // --- Simulated Annealing Loop ---
    let mut temp = hyperparameters.initial_temperature;
    let cooling_rate = hyperparameters.cooling_rate;
    let swap_prob = hyperparameters.swap_probability;

    for _ in 0..hyperparameters.max_iterations {
        if temp < 1e-8 {
            break;
        }

        let move_type = rng.gen::<f64>();
        let mut delta_value: i64 = 0;
        let new_weight: u32;

        // 1. Determine Move Type: Swap, Add, or Remove
        if move_type < swap_prob && !selected_indices.is_empty() && !unselected_indices.is_empty() {
            // --- SWAP MOVE (O(N)) ---
            let k_out_idx = rng.gen_range(0..selected_indices.len());
            let k_out = selected_indices[k_out_idx];

            let k_in_idx = rng.gen_range(0..unselected_indices.len());
            let k_in = unselected_indices[k_in_idx];

            let w_in = weights[k_in];
            let w_out = weights[k_out];

            // Check weight feasibility
            if w_in <= w_out {
                new_weight = current_weight - w_out + w_in;
            } else {
                if let Some(w) = current_weight.checked_sub(w_out).and_then(|w| w.checked_add(w_in)) {
                    if w <= max_weight {
                        new_weight = w;
                    } else {
                        continue; // Infeasible swap
                    }
                } else {
                    continue; // Overflow/Infeasible
                }
            }

            // Calculate incremental value change
            delta_value = values[k_in] as i64 - values[k_out] as i64;

            // Interaction change: Sum_{i in S\{k_out}} (I_{k_in, i} - I_{k_out, i})
            for &i in &selected_indices {
                if i != k_out {
                    delta_value += interactions[k_in][i] as i64;
                    delta_value -= interactions[k_out][i] as i64;
                }
            }

            // Acceptance criterion (Metropolis)
            if delta_value > 0 || rng.gen::<f64>() < f64::exp(delta_value as f64 / temp) {
                // Accept swap
                current_is_selected[k_out] = false;
                current_is_selected[k_in] = true;
                current_weight = new_weight;
                current_value += delta_value;

                // Update index vectors
                selected_indices.swap_remove(k_out_idx);
                selected_indices.push(k_in);
                unselected_indices.swap_remove(k_in_idx);
                unselected_indices.push(k_out);
            }

        } else if move_type < (swap_prob + (1.0 - swap_prob) / 2.0) && !unselected_indices.is_empty() {
            // --- ADD MOVE (O(N)) ---
            let k_in_idx = rng.gen_range(0..unselected_indices.len());
            let k_in = unselected_indices[k_in_idx];

            if let Some(w) = current_weight.checked_add(weights[k_in]) {
                if w <= max_weight {
                    new_weight = w;
                } else {
                    continue; // Infeasible add
                }
            } else {
                continue; // Overflow
            }

            // Calculate incremental value change: V_in + Sum_{i in S} I_{k_in, i}
            delta_value = values[k_in] as i64;
            for &i in &selected_indices {
                delta_value += interactions[k_in][i] as i64;
            }

            // Acceptance criterion
            if delta_value > 0 || rng.gen::<f64>() < f64::exp(delta_value as f64 / temp) {
                // Accept add
                current_is_selected[k_in] = true;
                current_weight = new_weight;
                current_value += delta_value;

                // Update index vectors
                selected_indices.push(k_in);
                unselected_indices.swap_remove(k_in_idx);
            }

        } else if !selected_indices.is_empty() {
            // --- REMOVE MOVE (O(N)) ---
            let k_out_idx = rng.gen_range(0..selected_indices.len());
            let k_out = selected_indices[k_out_idx];

            new_weight = current_weight - weights[k_out]; // Always feasible

            // Calculate incremental value change: -V_out - Sum_{i in S\{k_out}} I_{k_out, i}
            delta_value = -(values[k_out] as i64);
            for &i in &selected_indices {
                if i != k_out {
                    delta_value -= interactions[k_out][i] as i64;
                }
            }

            // Acceptance criterion
            if delta_value > 0 || rng.gen::<f64>() < f64::exp(delta_value as f64 / temp) {
                // Accept remove
                current_is_selected[k_out] = false;
                current_weight = new_weight;
                current_value += delta_value;

                // Update index vectors
                selected_indices.swap_remove(k_out_idx);
                unselected_indices.push(k_out);
            }
        }

        // Update best solution found so far
        if current_value > best_value {
            best_value = current_value;
            best_is_selected.copy_from_slice(&current_is_selected);
        }

        // Cooling schedule
        temp *= cooling_rate;
    }

    // Final selection based on the best solution found
    let selected: Vec<usize> = (0..num_items)
        .filter(|&i| best_is_selected[i])
        .collect();
    // EVOLVE-BLOCK-END

    save_solution(&Solution { items: selected })?;
    Ok(())
}

