                    match move_type {
                        0 => { // Swap (i out, j in)
                            let item_i_to_swap = selected[i_idx];
                            let item_j_to_swap = j_idx;

                            let delta_weight = challenge.weights[item_j_to_swap] as i64 - challenge.weights[item_i_to_swap] as i64;
                            total_weight = (total_weight as i64 + delta_weight) as u32;

                            is_selected[item_i_to_swap] = false;
                            is_selected[item_j_to_swap] = true;
                            selected[i_idx] = item_j_to_swap;

                            // Update interaction_sums efficiently in O(N).
                            for k in 0..num_items {
                                interaction_sums[k] += (challenge.interaction_values[k][item_j_to_swap] - challenge.interaction_values[k][item_i_to_swap]) as i64;
                            }
                        }
                        1 => { // Add (j in)
                            let item_j_to_add = j_idx;

                            total_weight += challenge.weights[item_j_to_add];
                            is_selected[item_j_to_add] = true;
                            selected.push(item_j_to_add);

                            // Update interaction_sums efficiently in O(N).
                            for k in 0..num_items {
                                interaction_sums[k] += challenge.interaction_values[k][item_j_to_add] as i64;
                            }
                        }
                        2 => { // Remove (i out)
                            let item_i_to_remove = selected[i_idx];

                            total_weight -= challenge.weights[item_i_to_remove];
                            is_selected[item_i_to_remove] = false;

                            // Swap the removed item with the last item and pop to maintain O(1) removal from Vec
                            selected.swap_remove(i_idx);

                            // Update interaction_sums efficiently in O(N).
                            for k in 0..num_items {
                                interaction_sums[k] -= challenge.interaction_values[k][item_i_to_remove] as i64;
                            }
                        }
                        _ => unreachable!(),
                    }
                }

                if !improvement_found {
                    break; // Local optimum reached.
                }
            }
        }
    }
    // EVOLVE-BLOCK-END

    save_solution(&Solution { items: selected })?;
    Ok(())
