use anyhow::{anyhow, Result};
use serde::{Deserialize, Serialize};
use serde_json::{Map, Value};
use rand::{rngs::SmallRng, Rng, SeedableRng};

pub fn solve_challenge(
    challenge: &Challenge,
    save_solution: &dyn Fn(&Solution) -> Result<()>,
    _hyperparameters: &Option<Map<String, Value>>,
) -> Result<()> {
    // EVOLVE-BLOCK-START    
    #[derive(Serialize, Deserialize)]
    pub struct Hyperparameters {
        // Optionally define hyperparameters here
    }

    // An O(N^2) greedy algorithm that considers interaction values.
    // At each step, it adds the item with the highest "marginal density".
    // Marginal density = (item's value + sum of interactions with already selected items) / item's weight.
    let num_items = challenge.values.len();

    let mut selected = Vec::new();
    let mut current_weight: u64 = 0;
    let mut is_selected = vec![false; num_items];
    // Use i64 for marginal gains to handle negative interactions and sums
    let mut marginal_gains: Vec<i64> = challenge.values.iter().map(|&v| v as i64).collect();

    loop {
        let mut best_item_idx: Option<usize> = None;
        let mut max_density = f64::NEG_INFINITY;

        // Find the best item to add from the unselected items
        for i in 0..num_items {
            if !is_selected[i] {
                if current_weight + challenge.weights[i] as u64 <= challenge.max_weight as u64 {
                    let weight = challenge.weights[i];
                    let gain = marginal_gains[i];

                    let density = if weight == 0 {
                        if gain > 0 { f64::INFINITY } else { f64::NEG_INFINITY }
                    } else {
                        gain as f64 / weight as f64
                    };

                    if density > max_density {
                        max_density = density;
                        best_item_idx = Some(i);
                    }
                }
            }
        }

        if let Some(idx_to_add) = best_item_idx {
            selected.push(idx_to_add);
            current_weight += challenge.weights[idx_to_add] as u64;
            is_selected[idx_to_add] = true;

            // Update marginal gains of remaining items based on the new item
            for i in 0..num_items {
                if !is_selected[i] {
                    marginal_gains[i] += challenge.interaction_values[idx_to_add][i] as i64;
                }
            }
        } else {
            // No more items can be added
            break;
        }
    }
    // EVOLVE-BLOCK-END

    save_solution(&Solution { items: selected })?;
    Ok(())
}

