use anyhow::{anyhow, Result};
use serde::{Deserialize, Serialize};
use serde_json::{Map, Value};
use rand::{rngs::SmallRng, Rng, SeedableRng};

pub fn solve_challenge(
    challenge: &Challenge,
    save_solution: &dyn Fn(&Solution) -> Result<()>,
    _hyperparameters: &Option<Map<String, Value>>,
) -> Result<()> {
    // EVOLVE-BLOCK-START    
    #[derive(Serialize, Deserialize)]
    pub struct Hyperparameters {
        // Optionally define hyperparameters here
    }

    // Iterative greedy construction heuristic that considers interaction values.
    // At each step, it adds the item with the highest marginal gain-to-weight ratio.
    let num_items = challenge.values.len();
    let mut selected = Vec::new();

    if num_items > 0 {
        let mut total_weight: u32 = 0;
        let mut is_selected = vec![false; num_items];
        // Use f64 for marginal gains to handle negative interactions and for ratio calculation.
        let mut marginal_gains: Vec<f64> = challenge.values.iter().map(|&v| v as f64).collect();

        loop {
            let mut best_item: Option<usize> = None;
            let mut best_ratio = f64::NEG_INFINITY;

            // Find the best item to add in this iteration.
            // The item's "value" is its base value plus the sum of interactions
            // with items already in the knapsack.
            for i in 0..num_items {
                if !is_selected[i] {
                    let item_weight = challenge.weights[i];
                    if total_weight + item_weight <= challenge.max_weight {
                        let ratio = if item_weight > 0 {
                            marginal_gains[i] / item_weight as f64
                        } else {
                            // For zero-weight items, prioritize by gain. A large constant ensures
                            // they are chosen over non-zero weight items if gain is positive.
                            if marginal_gains[i] > 0.0 {
                                f64::MAX / 2.0 + marginal_gains[i]
                            } else {
                                f64::NEG_INFINITY
                            }
                        };

                        if ratio > best_ratio {
                            best_ratio = ratio;
                            best_item = Some(i);
                        }
                    }
                }
            }

            if let Some(item_to_add) = best_item {
                selected.push(item_to_add);
                is_selected[item_to_add] = true;
                total_weight += challenge.weights[item_to_add];

                // Update marginal gains of remaining items based on the new item.
                // This avoids recomputing the full sum of interactions at each step.
                for i in 0..num_items {
                    if !is_selected[i] {
                        marginal_gains[i] += challenge.interaction_values[item_to_add][i] as f64;
                    }
                }
            } else {
                // No more items can be added that fit the weight constraint.
                break;
            }
        }
    }
    // EVOLVE-BLOCK-END

    save_solution(&Solution { items: selected })?;
    Ok(())
}

