                                delta_value = (challenge.values[item_j] as i64 - challenge.values[item_i] as i64)
                                                + (interaction_sums[item_j] - challenge.interaction_values[item_j][item_i] as i64)
                                                - interaction_sums[item_i];

                                if delta_value > 0 || rng.gen::<f64>() < (-delta_value as f64 / temperature).exp() {
